section .text
    global CMAIN
CMAIN:
    GET_UDEC 4, n
    GET_UDEC 4, k
   
    ;cmp dword[n], 1
    ;jnz .go
    ;cmp dword[k], 0
    ;jnz .go
    ;inc dword[result]
    ;jmp .end

;.go:
    mov eax, dword[n]
    test eax, eax
    jz .end

    mov eax, dword[k]
    cmp eax, 30
    jg .end
    
    bsr ecx, dword[n]
    test ecx, ecx
    jz .endl1
    dec ecx
    push dword[k]
.loop1:
    cmp ecx, 0
    jbe .endl1
    push ecx
    call c
    pop ecx
